declare type TurnstileOptions = {
    secret?: string;
    apiUrl?: string;
    hostname?: string | string[];
    remoteip?: string;
    action?: string | string[];
    cdata?: string | string[];
    debug?: boolean;
    throwOnFailure?: boolean;
};
declare type TurnstileResponse = {
    success: boolean;
    timestamp?: Date;
    hostname?: string;
    errors: string[];
    action?: string;
    cdata?: string;
};
declare function turnstile(secret?: string, globalOptions?: TurnstileOptions): (token: string, options?: TurnstileOptions) => Promise<TurnstileResponse>;
export default turnstile;
export { TurnstileOptions, TurnstileResponse };
